import enum


@enum.unique
class ProviderTypeEnum(enum.Enum):
    BigQuery = "BigQuery"
    Snowflake = "Snowflake"
    Databricks = "Databricks"
    Trino = "Trino"
    Redshift = "Redshift"
    ProphecyManaged = "ProphecyManaged"

    def getEnum(value):
        if value == "BigQuery":
            return ProviderTypeEnum.BigQuery
        elif value == "Snowflake":
            return ProviderTypeEnum.Snowflake
        elif value == "Databricks":
            return ProviderTypeEnum.Databricks
        elif value == "Trino":
            return ProviderTypeEnum.Trino
        elif value == "Redshift":
            return ProviderTypeEnum.Redshift
        else:
            return ProviderTypeEnum.ProphecyManaged
