from typing import Optional


def parseFloat(value: str) -> Optional[float]:
    try:
        return float(value)
    except:
        return None


def parseInt(value: str) -> Optional[int]:
    try:
        return int(value)
    except:
        return None


def isLong(value: str) -> bool:
    """
    :param value: number to be tested
    :return: false if input is within the range of [-2 ** 31, (2**31) - 1], true otherwise
    """
    try:
        minInt32 = -2 ** 31
        maxInt32 = ((2 ** 31) - 1)
        return not (minInt32 <= int(value) <= maxInt32)
    except ValueError:
        return False


def isLong(value: int) -> bool:
    """
    :param value: number to be tested
    :return: false if input is within the range of [-2 ** 31, (2**31) - 1], true otherwise
    """
    try:
        minInt32 = -2 ** 31
        maxInt32 = ((2 ** 31) - 1)
        return not (minInt32 <= value <= maxInt32)
    except ValueError:
        return False
